//solhint-disable max-line-length
//solhint-disable no-inline-assembly

contract CloneFactory {
  function createClone(address target) internal virtual returns (address result) {
    bytes20 targetBytes = bytes20(target);
    assembly {
      let clone := mload(0x40)
      mstore(clone, 0x3d602d80600a3d3981f3363d3d373d3d3d363d73000000000000000000000000)
      mstore(add(clone, 0x14), targetBytes)
      mstore(add(clone, 0x28), 0x5af43d82803e903d91602b57fd5bf30000000000000000000000000000000000)
      result := create(0, clone, 0x37)
    }
  }

  function isClone(address target, address query) internal view virtual returns (bool result) {
    bytes20 targetBytes = bytes20(target);
    assembly {
      let clone := mload(0x40)
      mstore(clone, 0x363d3d373d3d3d363d7300000000000000000000000000000000000000000000)
      mstore(add(clone, 0xa), targetBytes)
      mstore(add(clone, 0x1e), 0x5af43d82803e903d91602b57fd5bf30000000000000000000000000000000000)

      let other := add(clone, 0x40)
      extcodecopy(query, other, 0, 0x2d)
      result := and(
        eq(mload(clone), mload(other)),
        eq(mload(add(clone, 0xd)), mload(add(other, 0xd)))
      )
    }
  }
}

interface IEaselyContractInitializer {
  function init(bool[8] memory bools, address[8] memory addresses, uint256[32] memory uints, string[8] memory strings) external;
}

contract EaselyContractFactory is CloneFactory, Ownable {

  mapping(address => bool) private approverStatus;
  mapping(address => bool) private approvedImplementations;
  address[] private contracts;

  event ContractCreated(address creator, address contractAddress, address implementation, string easelyIdentifier);

  constructor(address[] memory implementations) {
    approverStatus[_msgSender()] = true;

    for (uint256 i = 0; i < implementations.length; i++) {
      approvedImplementations[implementations[i]] = true;
    }
  }

  function addApprover(address approver) external onlyOwner {
    approverStatus[approver] = true;
  }

  function removeApprover(address approver) external onlyOwner {
    approverStatus[approver] = false;
  }

  function approveImplementation(address implementation_) external {
    require(approverStatus[_msgSender()], "Must have approver status to approve a new implementation");
    approvedImplementations[implementation_] = true;
  }

  function removeImplementation(address implementation_) external {
    require(approverStatus[_msgSender()], "Must have approver status to remove an implementation");
    approvedImplementations[implementation_] = false;
  }

  function createContract(
    address implementation_,
    string memory easelyIdentifier,
    bool[8] memory bools, 
    address[8] memory addresses, 
    uint256[32] memory uints, 
    string[8] memory strings
  ) external {
    require(approvedImplementations[implementation_], "This is not an approved implementation");
    addresses[0] = _msgSender();

    address cloneAddr = createClone(implementation_);
    IEaselyContractInitializer newContract = IEaselyContractInitializer(cloneAddr);
    newContract.init(bools, addresses, uints, strings);

    contracts.push(cloneAddr);
    emit ContractCreated(msg.sender, cloneAddr, implementation_, easelyIdentifier);
  }

  function isApproved(address address_) external view returns (bool) {
    return approvedImplementations[address_];
  }

  function getContractAddress(uint256 index) external view returns (address) {
    return contracts[index];
  }
}
