pragma solidity 0.7.4;

contract PKcoin{
    int balance;
    constructor ()public{
        balance =0;
    }
    function getBalance() view public returns(int){
        return balance;
    }
    function depositBalance(int amount)  public {
        balance+=amount;
    }
     function withdrawBalance(int amount)  public {
        balance-=amount;
    }
}
