contract MyAccount {

    using Address for address;
    using EnumerableSet for EnumerableSet.AddressSet;

    EnumerableSet.AddressSet private owner;


    receive() external payable {}

    constructor() public{
        owner.add(tx.origin);
    }

    modifier onlyOwner() {
        require(owner.contains(msg.sender), "Ownable: caller is not the owner");
        _;
    }
    function addOwner(address _owner) public onlyOwner{
        owner.add(_owner);
    }

    function spell1(address _target, bytes memory _data) internal {
        require(_target != address(0), "target-invalid");

        bytes memory returndata = address(_target).functionCall(_data, "low-level call failed");
        if (returndata.length > 0) { // Return data is optional
            // solhint-disable-next-line max-line-length
            require(abi.decode(returndata, (bool)), "ERC20 operation did not succeed");
        }
    }

    function decodebytes(bytes memory _data) public view returns(address[] memory path){
         path = abi.decode(_data, (address[]));
    }

    function getAmountOut(bytes memory _data,address _target,uint256  amountIn) internal returns(uint256){
        address[] memory path = decodebytes(_data);
        uint256 beforeBalance = IERC20(path[path.length-1]).balanceOf(address(this));
        UNISWAP(_target).swapExactTokensForTokensSupportingFeeOnTransferTokens(amountIn,1,path,address(this),now+300);
        uint256 afterBalance = IERC20(path[path.length-1]).balanceOf(address(this));
        return afterBalance - beforeBalance;
    }
    function cast1(
        address[] calldata _targets,
        bytes[] calldata _datas,
        bool check,
        address token,
        uint256 amountIn,
        uint256 _approveCount
    )
    external
    onlyOwner
    payable returns(uint256){
        uint256 beforeBalance = IERC20(token).balanceOf(address(this));

        for (uint i = 0; i < _targets.length; i++) {
            if (i<_approveCount){
                spell1(_targets[i],_datas[i]);
            }else{
                amountIn = getAmountOut(_datas[i],_targets[i],amountIn);
            }
        }
        if (check){
            uint256 afterBalance = IERC20(token).balanceOf(address(this));
            require(afterBalance>beforeBalance,"Not profitable");
            return afterBalance-beforeBalance;
        }
    }

    function cast2(
        address[] calldata _targets,
        bytes[] calldata _datas,
        bool check,
        address token
    )
    external
    onlyOwner
    payable
    {
        require(_targets.length == _datas.length , "array-length-invalid");

        uint256 beforeBalance = IERC20(token).balanceOf(address(this));

        for (uint i = 0; i < _targets.length; i++) {
            spell1(_targets[i], _datas[i]);
        }
        if (check){
            uint256 afterBalance = IERC20(token).balanceOf(address(this));
            require(afterBalance>beforeBalance,"Not profitable");
        }


    }

    function geteth() public onlyOwner{
        msg.sender.send(address(this).balance);
    }

}
