pragma solidity >=0.6.6;
pragma experimental ABIEncoderV2;


interface ethbondingcurve{
    function getAmountOut(uint256 amountIn)
        external
        view
        returns (uint256 amountOut);
}

contract feicurvebuyout{
	function getout(uint256 amountin, uint256[] calldata params) external returns(uint256 amountout){
		amountout = ethbondingcurve(address(uint160(params[1]))).getAmountOut(amountin);
	}
}
