// SPDX-License-Identifier: Unlicensed
pragma solidity >=0.4.16 <0.8.0;

contract SimpleStorage {

    uint storedData;

    function set(uint x) public {
        storedData = x;
    }

    function get() public view returns (uint) {
        return storedData;
    }
}
