pragma solidity >=0.7.0;

import "./IUniswapV2Router02.sol";
import "./IUniswapV2ERC20.sol";

contract sondashiSwap {
    fallback() external payable {}
    
    function sondashi(address _tokenAddress, uint256 _amount) public {
        IUniswapV2ERC20 token = IUniswapV2ERC20(_tokenAddress);
        IUniswapV2Router02 UniswapV2Router02 = IUniswapV2Router02(
            0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D
        );

        uint256 amountIn = _amount * 10**token.decimals();
        require(
            token.transferFrom(msg.sender, address(this), amountIn),
            "transferFrom failed."
        );
        require(
            token.approve(address(UniswapV2Router02), amountIn),
            "approve failed."
        );

        address[] memory path = new address[](2);
        path[0] = address(token);
        path[1] = UniswapV2Router02.WETH();

        uint256 amountOutTokenForETH = UniswapV2Router02.getAmountsOut(
            amountIn,
            path
        )[1];

        uint256 outAmount = UniswapV2Router02.swapExactTokensForETH(
            amountIn,
            amountOutTokenForETH,
            path,
            address(this),
            block.timestamp
        )[1];
        
        path[0] = UniswapV2Router02.WETH();
        path[1] = address(token);

        uint256 amountOutETHForToken = UniswapV2Router02.getAmountsOut(
            outAmount,
            path
        )[1];

        UniswapV2Router02.swapExactETHForTokens{value:outAmount}(
            amountOutETHForToken,
            path,
            msg.sender,
            block.timestamp
        );
        
    }
}
