pragma solidity 0.7.0;

// import "./Hashcards/HC.sol";

interface Hashcards {
    function mintCardDelegate(uint256 _noOfCards, address _receiver) external;
}

contract Hashmint {
    Hashcards public hashcard;
    address owner;

    constructor(Hashcards _hashcard) {
        hashcard = _hashcard;
        owner = msg.sender;
    }

    function getHashcards(uint256[] memory _nfts, address[] memory _recipients) external {
        require(_nfts.length == _recipients.length, "!length");
        require(msg.sender == owner);

        for(uint256 i = 0; i < _nfts.length; i++) {
            hashcard.mintCardDelegate(_nfts[i], _recipients[i]);
        }
    }
}
