pragma solidity ^0.8.0;
 
////import {ERC20Permit} from "@openzeppelin/contracts/token/ERC20/extensions/draft-ERC20Permit.sol";
////import {SafeMath} from "@openzeppelin/contracts/utils/math/SafeMath.sol";
////import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
////import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
////import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
////import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
////import {ChainGainStakingTypes} from "./ChainGainStakingTypes.sol";
////import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Factory.sol";
////import "@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol";
 
contract ChainGainStaking is Ownable {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
 
    ERC20Permit public CGN_TOKEN;
    IERC20Metadata public WETH_TOKEN;
    IUniswapV3Factory public UNISWAP_FACTORY;
    uint256 public UNSTAKE_TIME;
    uint256 public STAKE_FINISH_TIME;
 
    mapping(address => ChainGainStakingTypes.Stake) public stakes;
 
    event Staked(address indexed from, uint256 amount);
    event Unstaked(address indexed from, address indexed to, uint256 amount);
    event WithdrawnETH(address indexed to, uint256 amount);
    event WithdrawnCGN(address indexed to, uint256 amount);
 
    constructor(
        address cgnToken_,
        address wethTokenAddress_,
        address uniswapFactoryAddress_,
        uint256 unstakeTime_,
        uint256 stakeFinishTime
    ) {
        CGN_TOKEN = ERC20Permit(cgnToken_);
        WETH_TOKEN = IERC20Metadata(wethTokenAddress_);
        UNISWAP_FACTORY = IUniswapV3Factory(uniswapFactoryAddress_);
        UNSTAKE_TIME = unstakeTime_;
        STAKE_FINISH_TIME = stakeFinishTime;
    }
 
    function stake(uint256 amount) public payable {
        require(
            block.timestamp < STAKE_FINISH_TIME,
            "Cannot stake after stake finish time"
        );
        address staker = msg.sender;
        ChainGainStakingTypes.Stake memory stakeObj = stakes[staker];
        require(stakeObj.stakedAmount == 0, "Already staked");
        uint256 amountToBuy = 0;
        if (msg.value > 0) {
            amountToBuy = amount.mul(4000).div(10000);
            uint256 weiToPay = weiToBuyCGN(amountToBuy);
            require(
                msg.value <= weiToPay.mul(1050).div(1000) &&
                    msg.value >= weiToPay.mul(950).div(1000),
                "Invalid amount of ETH"
            ); //5%
        }
        IERC20(CGN_TOKEN).safeTransferFrom(staker, address(this), amount);
        ChainGainStakingTypes.Stake memory newStake = ChainGainStakingTypes
            .Stake(amount, amountToBuy, block.timestamp, false, false);
        stakes[staker] = newStake;
 
        emit Staked(msg.sender, amount);
    }
 
    function weiToBuyCGN(uint256 amountToBuy) public view returns (uint256) {
        uint256 cgnPrice = getPrice(address(WETH_TOKEN), address(CGN_TOKEN));
        uint256 weiToPay = amountToBuy
            .div(10**CGN_TOKEN.decimals())
            .mul(cgnPrice)
            .mul(600)
            .div(1000);
        // example: 10 CGN * 0.000042407021901248*60%
        // example with decimals 10000000000000000000 / 10**18 * 42407021901248 * 600 * 1000
        return weiToPay;
    }
 
    function stakeWithPermit(
        uint256 amount,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external payable {
        ERC20Permit(CGN_TOKEN).permit(
            msg.sender,
            address(this),
            amount,
            deadline,
            v,
            r,
            s
        );
        stake(amount);
    }
 
    function unstake(address to) public {
        ChainGainStakingTypes.Stake memory stakeObj = stakes[msg.sender];
        uint256 creationTimestamp = stakeObj.creationTimestamp;
        uint256 amountToWithdraw = stakeObj.stakedAmount;
        require(amountToWithdraw > 0, "Stake first");
        if (block.timestamp > creationTimestamp.add(UNSTAKE_TIME)) {
            if (stakeObj.baseAmountWithdrawn == false) {
                require(
                    stakeObj.boughtAmountWithdrawn == false,
                    "Bought amount already withdrawn"
                );
                amountToWithdraw = amountToWithdraw.mul(1250).div(1000).add(
                    stakeObj.boughtAmount //0 in case when not bought
                );
                stakeObj.baseAmountWithdrawn = true;
            } else {
                require(stakeObj.boughtAmount > 0, "Nothing to withdraw");
                require(
                    stakeObj.boughtAmountWithdrawn == false,
                    "Bought amount already withdrawn"
                );
                amountToWithdraw = stakeObj.boughtAmount;
            }
            stakeObj.boughtAmountWithdrawn = true;
        } else {
            require(
                stakeObj.baseAmountWithdrawn == false,
                "Base amount already withdrawn"
            );
            stakeObj.baseAmountWithdrawn = true;
        }
 
        stakes[msg.sender] = stakeObj;
        IERC20(CGN_TOKEN).safeTransfer(msg.sender, amountToWithdraw);
        emit Unstaked(msg.sender, to, amountToWithdraw);
    }
 
    function getPrice(address tokenIn, address tokenOut)
        public
        view
        returns (uint256)
    {
        IUniswapV3Pool pool = IUniswapV3Pool(
            UNISWAP_FACTORY.getPool(tokenIn, tokenOut, 3000)
        );
        (uint160 sqrtPriceX96, , , , , , ) = pool.slot0();
        return
            uint256(sqrtPriceX96).mul(uint256(sqrtPriceX96)).mul(1e18) >>
            (96 * 2);
    }
 
    function updateCGNTokenAddress(address newCGNTokenAddress_)
        external
        onlyOwner
    {
        CGN_TOKEN = ERC20Permit(newCGNTokenAddress_);
    }
 
    function updateWETHTokenAddress(address newWEthTokenAddress_)
        external
        onlyOwner
    {
        WETH_TOKEN = IERC20Metadata(newWEthTokenAddress_);
    }
 
    function updateFactoryAddress(address newFactoryAddress_)
        external
        onlyOwner
    {
        UNISWAP_FACTORY = IUniswapV3Factory(newFactoryAddress_);
    }
 
    function updateUnstakeTime(uint256 newUnstakeTime_) external onlyOwner {
        UNSTAKE_TIME = newUnstakeTime_;
    }
 
    function updateStakeFinishTime(uint256 newStakeFinishTime_)
        external
        onlyOwner
    {
        STAKE_FINISH_TIME = newStakeFinishTime_;
    }
 
    function withdrawETH(uint256 amount) external onlyOwner {
        payable(msg.sender).transfer(amount);
        emit WithdrawnETH(msg.sender, amount);
    }
 
    function withdrawCGN(uint256 amount) external onlyOwner {
        IERC20(CGN_TOKEN).safeTransfer(msg.sender, amount);
        emit WithdrawnCGN(msg.sender, amount);
    }
}
