contract CryptoVoiceToken is Context, ERC20, ERC20Burnable, ERC20Snapshot, Ownable, ERC20Permit, ERC20Votes  {
	string TOKEN_NAME = "CryptoVoice Dollar";
	string TOKEN_SYMBOL = "CVD";
	uint256 public constant MAX_SUPPLY = 1_000_000_000 * 10**18;
	address _defiContract;
	address _nftContract;
	mapping(address => uint256) public _activityPoint;
	

	modifier onlyDefiContract() {
		require(_defiContract == _msgSender());
		_;
	}

	modifier onlyNftContract() {
		require(_nftContract == _msgSender());
		_;
	}

	constructor() ERC20(TOKEN_NAME, TOKEN_SYMBOL) ERC20Permit(TOKEN_SYMBOL) {}

	function setDefiContract(address defiContract_) public onlyOwner {
		_defiContract = defiContract_;
	}

	function setNftContract(address nftContract_) public onlyOwner {
		_nftContract = nftContract_;
	}

	function snapshot() public onlyOwner {
		_snapshot();
	}

	function getPoint(address account) public view returns (uint256) {
		return _activityPoint[account] ;
	}

	function setPoint(address account, uint256 amount) external onlyNftContract {
		_activityPoint[account] = amount;
	}

	//for activity point update
	function setPointList(address[] memory userList_, uint256[] memory pointList_) public onlyOwner {
		require(userList_.length == pointList_.length, "input error");
		for (uint256 i = 0; i < userList_.length; ++i) {
			_activityPoint[userList_[i]] = pointList_[i];  
		}
	}

    //for activity point mint
	function pointClaim(address to, uint256 amount) public {
        require(_msgSender() == to, "you can only claim your reserved point");
		require(_activityPoint[to] >= amount, "not enough point");
		_mint(to, amount);
		_activityPoint[to] -= amount;  
	}

	function mint(address to, address dev, uint256 amount) external onlyDefiContract {
		_mint(to, amount);
        _mint(dev, amount/10);
	}

	function burn(address to, uint256 amount) external onlyNftContract {
		_burn(to, amount);
	}

	//Emergency WithDraw
	function withdrawAll() public payable onlyOwner {
        require(payable(msg.sender).send(address(this).balance));
    }
    //Emergency token withdraw
    function withdrawTokenAll() public onlyOwner {
        _transfer(address(this), _msgSender(), balanceOf(address(this)));
    }
    
	// The following functions are overrides required by Solidity.

    
	function _beforeTokenTransfer(
		address from,
		address to,
		uint256 amount
	) internal override(ERC20, ERC20Snapshot) {
		super._beforeTokenTransfer(from, to, amount);
	}

	function _afterTokenTransfer(
		address from,
		address to,
		uint256 amount
	) internal override(ERC20, ERC20Votes) {
		super._afterTokenTransfer(from, to, amount);
	}

	function _mint(address to, uint256 amount) internal override(ERC20, ERC20Votes) {
        require (totalSupply() + amount <= MAX_SUPPLY, "can not mint over max supply");
		super._mint(to, amount);
	}

	function _burn(address account, uint256 amount) internal override(ERC20, ERC20Votes) {
		super._burn(account, amount);
	}
}

