        }
        if (to == address(0)) {
            _removeTokenFromAllTokensEnumeration(tokenId);
        } else if (to != from) {
            _addTokenToOwnerEnumeration(to, tokenId);
        }
    }

    /**
     * @dev Private function to add a token to this extension's ownership-tracking data structures.
     * @param to address representing the new owner of the given token ID
     * @param tokenId uint256 ID of the token to be added to the tokens list of the given address
     */
    function _addTokenToOwnerEnumeration(address to, uint256 tokenId) private {
        uint256 length = ERC721.balanceOf(to);
        _ownedTokens[to][length] = tokenId;
        _ownedTokensIndex[tokenId] = length;
    }

    /**
     * @dev Private function to add a token to this extension's token tracking data structures.
     * @param tokenId uint256 ID of the token to be added to the tokens list
     */
    function _addTokenToAllTokensEnumeration(uint256 tokenId) private {
        _allTokensIndex[tokenId] = _allTokens.length;
        _allTokens.push(tokenId);
    }

    /**
     * @dev Private function to remove a token from this extension's ownership-tracking data structures. Note that
     * while the token is not assigned a new owner, the `_ownedTokensIndex` mapping is _not_ updated: this allows for
     * gas optimizations e.g. when performing a transfer operation (avoiding double writes).
     * This has O(1) time complexity, but alters the order of the _ownedTokens array.
     * @param from address representing the previous owner of the given token ID
     * @param tokenId uint256 ID of the token to be removed from the tokens list of the given address
     */
    function _removeTokenFromOwnerEnumeration(address from, uint256 tokenId) private {
        // To prevent a gap in from's tokens array, we store the last token in the index of the token to delete, and
        // then delete the last slot (swap and pop).

        uint256 lastTokenIndex = ERC721.balanceOf(from) - 1;
        uint256 tokenIndex = _ownedTokensIndex[tokenId];

        // When the token to delete is the last token, the swap operation is unnecessary
        if (tokenIndex != lastTokenIndex) {
            uint256 lastTokenId = _ownedTokens[from][lastTokenIndex];

            _ownedTokens[from][tokenIndex] = lastTokenId; // Move the last token to the slot of the to-delete token
            _ownedTokensIndex[lastTokenId] = tokenIndex; // Update the moved token's index
        }

        // This also deletes the contents at the last position of the array
        delete _ownedTokensIndex[tokenId];
        delete _ownedTokens[from][lastTokenIndex];
    }

    /**
     * @dev Private function to remove a token from this extension's token tracking data structures.
     * This has O(1) time complexity, but alters the order of the _allTokens array.
     * @param tokenId uint256 ID of the token to be removed from the tokens list
     */
    function _removeTokenFromAllTokensEnumeration(uint256 tokenId) private {
        // To prevent a gap in the tokens array, we store the last token in the index of the token to delete, and
        // then delete the last slot (swap and pop).

        uint256 lastTokenIndex = _allTokens.length - 1;
        uint256 tokenIndex = _allTokensIndex[tokenId];

        // When the token to delete is the last token, the swap operation is unnecessary. However, since this occurs so
        // rarely (when the last minted token is burnt) that we still do the swap here to avoid the gas cost of adding
        // an 'if' statement (like in _removeTokenFromOwnerEnumeration)
        uint256 lastTokenId = _allTokens[lastTokenIndex];

        _allTokens[tokenIndex] = lastTokenId; // Move the last token to the slot of the to-delete token
        _allTokensIndex[lastTokenId] = tokenIndex; // Update the moved token's index

        // This also deletes the contents at the last position of the array
        delete _allTokensIndex[tokenId];
        _allTokens.pop();
    }
}

interface ICryptoVoiceDefi {
    function add(address user, uint256 pid) external;
    function transferNft(address from_, address to_, uint256 count) external; 
}

contract CryptoVoiceNft is Context, ERC721, ERC721Enumerable, Ownable {
	string TOKEN_NAME = "CryptoVoice NFT";
	string TOKEN_SYMBOL = "CVNFT";
	uint256 public constant MAX_NFTS = 5555;
	uint256 public _preSaleAmount = 2000;

    uint256[MAX_NFTS] internal indices;
	uint256 internal nonce = 0;

	string private _baseTokenURI = "https://cryptovoice.me/temp/"; 

	uint256 public _etherPrice = 55_000_000_000_000_000;
	uint256 public _tokenPrice = 1000_000_000_000_000_000_000;
  	
	uint256 public round = 0;
	uint256 public blockPerRound = 2888; // 1 day 
	uint256 public roundStartBlock;
    uint256 public _roundSalePerRound = 50;
	
	//discount rate
	uint256 public goldDiscountRate = 10;
	uint256 public batchDiscountRate = 5;

    uint256 public _referralFee = 110;

	ICryptoVoiceToken public _cvToken;
	ICryptoVoiceDefi public _cvDefi;

	MarketStatus public _marketStatus;
	
	enum MarketStatus {
        PreHolderSale,
        PrePublicSale,
        RoundSale,
		SaleStop
    }

	enum PayType {
		Ether,
		CVToken
	}
	
	mapping(address => uint256) public _keyHolderList;

	constructor(address cvTokenAddress_, address[] memory keyHolderList_, uint256[] memory rankList_, MarketStatus marksetStatus_) ERC721(TOKEN_NAME, TOKEN_SYMBOL) {
		_marketStatus = marksetStatus_;
		_cvToken = ICryptoVoiceToken(cvTokenAddress_);
		addKeyHolderList(keyHolderList_, rankList_); 
	}

	function setCVDefi(address _cvDefiAddress) public onlyOwner {
		_cvDefi = ICryptoVoiceDefi(_cvDefiAddress);
	}

	function addKeyHolderList(address[] memory keyHolderList_, uint256[] memory rankList_) public onlyOwner {
		for (uint256 i = 0; i < keyHolderList_.length; ++i) {
			_keyHolderList[keyHolderList_[i]] = rankList_[i];
		} 
	}

	function setDiscountRate(uint256[2] memory rateList) public onlyOwner {
		goldDiscountRate = rateList[0];
		batchDiscountRate = rateList[1];
	}
	
    function setReferralfee(uint256 referralFee_) public onlyOwner {
        _referralFee = referralFee_;
    }

	function setMarketStatus(MarketStatus marketStatus_) public onlyOwner {
		_marketStatus = marketStatus_;
		if (marketStatus_ == MarketStatus.RoundSale) {
			//start Round Block 
			roundStartBlock = block.number;
		}
	}

	function setTokenPrice(uint256 price_, PayType payType_) public onlyOwner {
		if (payType_ == PayType.Ether) {
			_etherPrice = price_;
		} else {
			_tokenPrice = price_;
		}
	}

	function setPreSaleAmount(uint256 preSaleAmount_ ) public onlyOwner {
		_preSaleAmount = preSaleAmount_;
	}
    
	function preMint(address to, uint256 count, PayType payType, address referrer) public payable {
		require(_marketStatus != MarketStatus.SaleStop, "sale stoppped");
		require(totalSupply() + count <= _preSaleAmount, "over max limit");	
		if (_marketStatus == MarketStatus.PreHolderSale) {
			require(_keyHolderList[_msgSender()] > 0, "Only key holder can mint");
		}
		mint(to, count, payType, referrer);
    }

	function roundMint(address to, uint256 count, PayType payType, address referrer) public payable {
		require(_marketStatus == MarketStatus.RoundSale, "Not in the round-sale period");
		require(totalSupply() + count <= _preSaleAmount + calculateRoundNFT(), "over max limit");
		mint(to, count, payType, referrer);
	}

	function calculateRoundNFT() internal view returns (uint256){
		require (roundStartBlock < block.number, "error!");
        uint256 roundDay = (roundStartBlock - block.number) / blockPerRound ;
		return _roundSalePerRound * roundDay;  
	}

	function mint(address to, uint256 count, PayType payType, address referrer) internal {
		uint256 finalPrice;
        // can not referrer ownself
		if (referrer != address(0) && to != referrer ) { 	
			uint256 beforePoint = _cvToken.getPoint(referrer);
			_cvToken.setPoint(referrer, beforePoint + (_referralFee * count * 10**18)); 
		}
		if (payType == PayType.Ether) {
			finalPrice = calculatePrice(_etherPrice, count, _keyHolderList[_msgSender()]);
			// pay by ether
			require(msg.value >= finalPrice, "Value below price");
		} else if (payType == PayType.CVToken) {
			finalPrice = calculatePrice (_tokenPrice, count, _keyHolderList[_msgSender()]);
			// pay by CV Token
			require(_cvToken.balanceOf(_msgSender()) >= finalPrice, "not enough CVD");
			// burn CVtoken
			_cvToken.burn(_msgSender(),  finalPrice);
		} else {
			revert("wrong payType");
		}
		mulitpleMint(to, count);
	}

	function calculatePrice (uint256 price, uint256 count, uint256 isGold) public view returns (uint256) {
		uint256 goldMultiply = 0;
		uint256 batchMultiply = 0; 
		if (count >= 20) {
			batchMultiply = 3;
		} else if (count >= 10) {
			batchMultiply = 2;
		} else if (count >= 5){
			batchMultiply = 1; 
		} 
		if (isGold == 2) {
			goldMultiply = 1;
		}
		return price * count * (100 - batchMultiply * batchDiscountRate) * (100 - goldMultiply * goldDiscountRate) / 10000;
	}

	function mulitpleMint(address to, uint256 count) internal {
        require(totalSupply() + count <= MAX_NFTS, "over max limit");
        //update defi info
        _cvDefi.transferNft(address(0), to, count);
		for (uint256 i = 0; i < count; i += 1) {
			_safeMint(to, _generateRandomIndex());
		}
    }

	function withdrawAll() public payable onlyOwner {
        require(payable(_msgSender()).send(address(this).balance));
    }
	
    function walletOfOwner(address _owner)
        external
        view
        returns (uint256[] memory)
    {
        uint256 tokenCount = balanceOf(_owner);
        uint256[] memory tokensId = new uint256[](tokenCount);
        for (uint256 i = 0; i < tokenCount; i++) {
            tokensId[i] = tokenOfOwnerByIndex(_owner, i);
        }
        return tokensId;
    }
	/**
	 * Override isApprovedForAll to auto-approve OS's proxy contract
	 */
	function isApprovedForAll(address _owner, address _operator) public view override returns (bool isOperator) {
		// otherwise, use the default ERC721.isApprovedForAll()
		return ERC721.isApprovedForAll(_owner, _operator);
	}

	function supportsInterface(bytes4 interfaceId) public view virtual override( ERC721, ERC721Enumerable) returns (bool) {
		return super.supportsInterface(interfaceId);
	}

	function _baseURI() internal view virtual override returns (string memory) {
		return _baseTokenURI;
	}

	function setBaseURI(string memory baseURI) public onlyOwner {
		_baseTokenURI = baseURI;
	}

    function _transfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override {
        super._transfer(from, to, tokenId);
    }

	function _beforeTokenTransfer(
		address from,
		address to,
		uint256 tokenId
	) internal virtual override(ERC721, ERC721Enumerable) {
		if (from != address(0) && to != address(0)) {
			//update defi info
            _cvDefi.transferNft(from, to, 1);
		}
		super._beforeTokenTransfer(from, to, tokenId);
	}

    function _generateRandomIndex() internal returns (uint256) {
        uint256 totalSize = MAX_NFTS - totalSupply();
        uint256 index = uint256(
            keccak256(
                abi.encodePacked(
                    nonce,
                    _msgSender(),
                    block.difficulty,
                    block.timestamp
                )
            )
        ) % totalSize;

        uint256 value = 0;
        if (indices[index] != 0) {
            value = indices[index];
        } else {
            value = index;
        }

        // Move last value to selected position
        if (indices[totalSize - 1] == 0) {
            // Array position not initialized, so use position
            indices[index] = totalSize - 1;
        } else {
            // Array position holds a value so use that
            indices[index] = indices[totalSize - 1];
        }
        nonce++;
        // Don't allow a zero index, start counting at 1
        return value++;
    }
}


interface IMigratorCV {
    // Perform LP token migration from legacy Defi to new Defi.
    // Take the current LP token address and return the new LP token address.
    // Migrator should have full access to the caller's LP token.
    // Return the new LP token address.
    //
    // XXX Migrator must have allowance access to CryptoVoice LP tokens.
    function migrate(IERC20 token) external returns (IERC20);
}

contract CryptoVoiceDefi is Ownable {
    using SafeERC20 for IERC20;
    using Counters for Counters.Counter;
    // Info of each user. 
    struct UserInfo {
        uint256 amount;     // How many CV tokens the user has provided.
        uint256 rewardDebt; // accumulated debt
    }
    // Info of each pool.
    struct PoolInfo {  
        IERC20 lpToken;
        uint256 allocPoint;       // How many allocation points assigned to this pool.
        uint256 lastRewardBlock;  // Last block number that CV Token distribution occurs.
        uint256 accCVTokenPerShare; // Accumulated Tokens.
    }

    CryptoVoiceNft public _cvNft;
    CryptoVoiceToken public _cvToken;
    
    uint256 public _cvTokenPerBlock;
    uint256 public _startBlock; // The block number when CV Token mining starts.

    Counters.Counter private _poolIdTracker;
    mapping (uint256 => PoolInfo) public poolInfo;
    mapping (uint256 => mapping (address => UserInfo)) public userInfo;


    uint256 public _totalAllocPoint = 0;
    
    uint256 private ACC_CV_TOKEN_PRECISION = 1e18;
    address public _devAddr;
    IMigratorCV public _migrator;
    
    event Deposit(address indexed user, uint256 indexed pid, uint256 amount);
    event Withdraw(address indexed user, uint256 indexed pid, uint256 amount);
    event EmergencyStakeWithdraw(address indexed user, uint256 amount);

    constructor(
        CryptoVoiceToken cvToken_,
        CryptoVoiceNft cvNft_,
        uint256 cvTokenPerBlock_,
        uint256 startBlock_
    ) {
        _cvToken = cvToken_;
        _cvNft = cvNft_;
        _cvTokenPerBlock = cvTokenPerBlock_;
        _startBlock = startBlock_;
        _devAddr = _msgSender();
        
        // staking pool init 
        poolInfo[_poolIdTracker.current()] = PoolInfo({
            lpToken: cvToken_,
            allocPoint: 1000,
            lastRewardBlock: startBlock_,
            accCVTokenPerShare: 0
        });
        _poolIdTracker.increment();
        // nft pool init 
        poolInfo[_poolIdTracker.current()] = PoolInfo({
            lpToken: _cvToken, // dummy token
            allocPoint: 2000,
            lastRewardBlock: startBlock_,
            accCVTokenPerShare: 0
        });
        _totalAllocPoint = 3000;
        _poolIdTracker.increment();
    }

    modifier onlyNftContract() {
		require(_msgSender() == address(_cvNft) , "msg sender not nft contract");
		_;
	}

    function setDevrAddr(address devAddr_) public onlyOwner {
        _devAddr = devAddr_;
    }

    // Set the migrator contract. Can only be called by the owner.
    function setMigrator(IMigratorCV migrator_) public onlyOwner {
        _migrator = migrator_;
    }

    // Migrate lp token to another lp contract. Can be called by anyone. We trust that migrator contract is good.
    function migrate(uint256 _pid) public {
        require(address(_migrator) != address(0), "migrate: no migrator");
        PoolInfo storage pool = poolInfo[_pid];
        IERC20 lpToken = pool.lpToken;
        uint256 bal = lpToken.balanceOf(address(this));
        lpToken.safeApprove(address(_migrator), bal);
        IERC20 newLpToken = _migrator.migrate(lpToken);
        require(bal == newLpToken.balanceOf(address(this)), "migrate: bad");
        pool.lpToken = newLpToken;
    }

    function setCVToken(address cvToken_) public onlyOwner {
		_cvToken = CryptoVoiceToken(cvToken_);
	}

	function setCVNft(address cvNft_) public onlyOwner {
		_cvNft = CryptoVoiceNft(cvNft_);
	}

    function setCVTokenPerBlock(uint256 cvTokenPerBlock_) public onlyOwner {
        _cvTokenPerBlock = cvTokenPerBlock_;
    }

    // Add a new lp to the pool. Can only be called by the owner.
    // XXX DO NOT add the same LP token more than once. Rewards will be messed up if you do.
    function addPool(uint256 _allocPoint, IERC20 _lpToken, bool _withUpdate) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 lastRewardBlock = block.number > _startBlock ? block.number : _startBlock;
        _totalAllocPoint +=  _allocPoint;
     // nft pool init 
        poolInfo[_poolIdTracker.current()] = PoolInfo({
            lpToken: _lpToken,
            allocPoint: _allocPoint,
            lastRewardBlock: lastRewardBlock,
            accCVTokenPerShare: 0
        });
        _poolIdTracker.increment();
        updateStakingPool();
    }

    // Set the given pool
    function setPool(uint256 _pid, uint256 _allocPoint, bool _withUpdate) public onlyOwner {
        if (_withUpdate) {
            massUpdatePools();
        }
        uint256 prevAllocPoint = poolInfo[_pid].allocPoint;
        poolInfo[_pid].allocPoint = _allocPoint;
        if (prevAllocPoint != _allocPoint) {
            _totalAllocPoint -= prevAllocPoint;
            _totalAllocPoint += _allocPoint;
        }
        updateStakingPool();
    }

    // Update reward variables for all pools. Be careful of gas spending!
    function massUpdatePools() public {
        uint256 length = _poolIdTracker.current();
        for (uint256 pid = 0; pid < length; ++pid) {
            updatePool(pid);
        }
    }

    function updateStakingPool() internal {
        uint256 length = _poolIdTracker.current();
        uint256 points = 0;
        for (uint256 pid = 1; pid < length; ++pid) {
            points += poolInfo[pid].allocPoint;
        }
        if (points != 0) {
            points /= 3;
            _totalAllocPoint = _totalAllocPoint - poolInfo[0].allocPoint + points;
            poolInfo[0].allocPoint = points;
        }
    }

    /// @notice View function to see pending CV Token on frontend.
    /// @param pid The index of the pool. See `poolInfo`.
    /// @param user_ Address of user.
    /// @return pending CV token reward for a given user.
    function pendingCVToken(uint256 pid, address user_) public view returns (uint256) {
        PoolInfo memory pool = poolInfo[pid];
        UserInfo memory user = userInfo[pid][user_];
        uint256 accCVTokenPerShare = pool.accCVTokenPerShare;
        uint256 pending = 0; 
        if (block.number > pool.lastRewardBlock) {
            uint256 blocks = block.number - pool.lastRewardBlock;
            uint256 lpSupply;
            uint256 userAmount;
            if (pid == 1) { // for nft
                lpSupply = _cvNft.totalSupply();
                userAmount = _cvNft.balanceOf(user_);
            } else {
                lpSupply = pool.lpToken.balanceOf(address(this));
                userAmount = user.amount;
            }
            if (lpSupply !=  0) {
                uint256 cvTokenReward = blocks * _cvTokenPerBlock * pool.allocPoint / _totalAllocPoint;  
                accCVTokenPerShare += (cvTokenReward * ACC_CV_TOKEN_PRECISION) / lpSupply;
                pending = (userAmount * accCVTokenPerShare / ACC_CV_TOKEN_PRECISION) - user.rewardDebt;
            }
        }
        return pending;
    }

    /// @notice Update reward variables of the given pool.
    /// @param pid The index of the pool. See `poolInfo`.
    function updatePool(uint256 pid) public returns (uint256) {
        PoolInfo storage pool = poolInfo[pid];
        if (block.number <= pool.lastRewardBlock) {
            return pool.accCVTokenPerShare; 
        }
        uint256 cvTokenReward = 0; 
        uint256 blocks = block.number - pool.lastRewardBlock;    
        uint256 lpSupply; 
        if (pid == 1) {
            lpSupply = _cvNft.totalSupply();
        } else {
            lpSupply = pool.lpToken.balanceOf(address(this));
        }
        if (lpSupply ==  0) {
            pool.lastRewardBlock = block.number;
            return pool.accCVTokenPerShare;
        }
        cvTokenReward = blocks * _cvTokenPerBlock * pool.allocPoint / _totalAllocPoint;
        pool.accCVTokenPerShare += ((cvTokenReward * ACC_CV_TOKEN_PRECISION) / lpSupply);
        pool.lastRewardBlock = block.number; 
        return pool.accCVTokenPerShare;   
    }


    /// @notice claim CV token before CV NFT trasnfer or minting
    /// transfer : from -> to  count nft 
    /// mint : 0 -> to count nft 
    function transferNft(address from_, address to_, uint256 count) external onlyNftContract {
        uint256 accCVTokenPerShare = updatePool(1);  
        if (from_ != address(0)) {
            UserInfo storage fromUser = userInfo[1][from_];
            uint256 fromWalletLength = _cvNft.balanceOf(from_);
            uint256 fromNftReward = fromWalletLength * accCVTokenPerShare / ACC_CV_TOKEN_PRECISION - fromUser.rewardDebt;
            if (fromNftReward > 0) {
                _cvToken.mint(from_, _devAddr, fromNftReward);
            }
            fromWalletLength -= count;
            fromUser.rewardDebt = fromWalletLength * accCVTokenPerShare / ACC_CV_TOKEN_PRECISION ;
        }
        UserInfo storage toUser = userInfo[1][to_];
        uint256 toWalletLength = _cvNft.balanceOf(to_); 
        uint256 toNftReward = toWalletLength * accCVTokenPerShare / ACC_CV_TOKEN_PRECISION - toUser.rewardDebt;
        if (toNftReward > 0) {
            _cvToken.mint(to_, _devAddr, toNftReward);
        }
        toWalletLength += count;
        toUser.rewardDebt = toWalletLength * accCVTokenPerShare / ACC_CV_TOKEN_PRECISION ;
    }

    /// @notice Claim CV token of NFT for user order
    function claimAllCVToken() public {
        UserInfo storage user = userInfo[1][_msgSender()];
        uint256 walletLength = _cvNft.balanceOf(_msgSender());
        uint256 accCVTokenPerShare = updatePool(1);
        uint256 nftReward = walletLength * accCVTokenPerShare / ACC_CV_TOKEN_PRECISION - user.rewardDebt;
        if (nftReward > 0) {
            _cvToken.mint(_msgSender(), _devAddr, nftReward);  
            user.rewardDebt = walletLength * accCVTokenPerShare / ACC_CV_TOKEN_PRECISION;
        }
    }

    // Stake CV tokens to CV Defi
    function enterStaking(uint256 amount) public {
        PoolInfo storage pool = poolInfo[0];
        UserInfo storage user = userInfo[0][_msgSender()];
        
        updatePool(0);
        if (user.amount > 0) {
            uint256 pending = (user.amount * pool.accCVTokenPerShare / ACC_CV_TOKEN_PRECISION) - (user.rewardDebt);
            if(pending > 0) {
                _cvToken.mint(_msgSender(), _devAddr, pending);
            }
        }
        if(amount > 0) {
            pool.lpToken.safeTransferFrom(address(_msgSender()), address(this), amount);
            user.amount += amount;
        }
        user.rewardDebt = user.amount * pool.accCVTokenPerShare / ACC_CV_TOKEN_PRECISION;

        emit Deposit(_msgSender(), 0, amount);
    }

    // Withdraw CV tokens from CV token staking.
    function leaveStaking(uint256 amount) public {
        PoolInfo storage pool = poolInfo[0];
        UserInfo storage user = userInfo[0][_msgSender()];
        require(user.amount >= amount, "don't have CV token enough to withdraw");

        updatePool(0);
        uint256 pending = (user.amount * pool.accCVTokenPerShare / ACC_CV_TOKEN_PRECISION) - (user.rewardDebt);
        if(pending > 0) {
            _cvToken.mint(_msgSender(), _devAddr, pending);
        }
        if(amount > 0) {
            user.amount -= amount;
            pool.lpToken.safeTransfer(address(_msgSender()), amount);
        }
        user.rewardDebt = user.amount * pool.accCVTokenPerShare / ACC_CV_TOKEN_PRECISION;

        emit Withdraw(_msgSender(), 0, amount);
    }

    // Deposit LP tokens to CV defi for CV Token allocation.
    function deposit(uint256 pid, uint256 amount) public {
        require (pid != 0, "deposit CV Token by LP token staking");
        require (pid != 1, "can't depoist CV NFT ");
        PoolInfo storage pool = poolInfo[pid];
        UserInfo storage user = userInfo[pid][_msgSender()];

        updatePool(pid);
        if (user.amount > 0) {
            uint256 pending = user.amount * pool.accCVTokenPerShare / ACC_CV_TOKEN_PRECISION - user.rewardDebt;
            if(pending > 0) {
                _cvToken.mint(_msgSender(), _devAddr, pending);
            }
        }
        if (amount > 0) {
            pool.lpToken.safeTransferFrom(address(_msgSender()), address(this), amount);
            user.amount += amount;
        }
        user.rewardDebt = user.amount * pool.accCVTokenPerShare / ACC_CV_TOKEN_PRECISION;
        emit Deposit(_msgSender(), pid, amount);
    }

    // Withdraw LP tokens from MasterChef.
    function withdraw(uint256 pid, uint256 amount) public {
        require (pid != 0, 'withdraw CAKE by unstaking');
        require (pid != 1, "can't depoist CV NFT ");
        PoolInfo storage pool = poolInfo[pid];
        UserInfo storage user = userInfo[pid][_msgSender()];
        require(user.amount >= amount, "withdraw: not good");

        updatePool(pid);
        uint256 pending = user.amount * pool.accCVTokenPerShare / ACC_CV_TOKEN_PRECISION - user.rewardDebt;
        if(pending > 0) {
            _cvToken.mint(_msgSender(), _devAddr, pending);
        }
        if(amount > 0) {
            user.amount -= amount;
            pool.lpToken.safeTransfer(address(_msgSender()), amount);
        }
        user.rewardDebt = user.amount * pool.accCVTokenPerShare / ACC_CV_TOKEN_PRECISION;
        emit Withdraw(_msgSender(), pid, amount);
    }


    // Withdraw from staking without caring about rewards. EMERGENCY ONLY.
    function emergencyWithdraw(uint256 pid) public {
        require (pid != 1, "cv nft can not withdraw");
        PoolInfo storage pool = poolInfo[pid];
        UserInfo memory user = userInfo[pid][_msgSender()];
        pool.lpToken.safeTransfer(address(_msgSender()), user.amount);
        emit EmergencyStakeWithdraw(_msgSender(), user.amount);
        user.amount = 0;
        user.rewardDebt = 0;
    }

    function withdrawAll() public payable onlyOwner {
        require(payable(_msgSender()).send(address(this).balance));
    }
}
